import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { supabase } from '../client';
import './DetailView.css';
import { formatDistanceToNow } from 'date-fns';

const DetailView = () => {
  const { id } = useParams();
  const [post, setPost] = useState({});
  const [count, setCount] = useState(0);
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]); // State to store fetched comments
  // Step 1: State for comment text

  useEffect(() => {
    const fetchPost = async () => {
      const { data } = await supabase
        .from('Posts')
        .select()
        .eq('id', id)
        .single();

      setPost(data);
    };

    fetchPost();
  }, [id]);

  useEffect(() => {
    const fetchCount = async () => {
      const { data } = await supabase
        .from('Posts')
        .select('upvotes')
        .eq('id', id)
        .single();

      setCount(data.upvotes);
    };

    fetchCount();
  }, [id]);

  const updateCount = async (event) => {
    event.preventDefault();

    await supabase
      .from('Posts')
      .update({ upvotes: count + 1 })
      .eq('id', id);

    setCount((count) => count + 1);
  };

  const deletePost = async (event) => {
    event.preventDefault();
    await supabase
      .from('Posts')
      .delete()
      .eq('id', id);
    window.location = "/read";
  };

  const submitComment = async (event) => {
    event.preventDefault(); // Prevent the default form submission behavior
    if (!comment.trim()) return; // Ignore empty comments

    try {
      const { error } = await supabase
        .from('Comments') // Ensure this is the correct table name
        .insert([
          { postId: id, comment: comment } // Assuming 'postId' is the correct field name
        ]);

      setComments((comments) => [...comments, { comment }]); // Update the comments list with the new comment
      setComment(""); // Clear the comment input field
      // Optionally, refresh comments list here
    } catch (error) {
      console.error('Error submitting comment:', error.message);
      // Handle the error appropriately (e.g., display a message to the user)
    }
  };

  return (
    <div className="DetailView">
      {post.created_at && (
        <p>Posted {formatDistanceToNow(new Date(post.created_at), { addSuffix: true })}</p>
      )}
      <h2>{"Title: " + post.title}</h2>
      {post.image && (<img src={post.image} alt={post.title} />)}
      <h3>Info:</h3>
      <p>{"Author: " + post.author}</p>
      <p>{"Description: " + post.description}</p>
      <button onClick={updateCount}>Upvotes: {count}</button>
      <Link to={`/edit/${id}`}><button> üñãÔ∏è</button></Link>
      <button onClick={deletePost}> üóëÔ∏è</button>
      <div>
        {comments.map((comment, index) => (
          <p key={index}>{comment.comment}</p> // Assuming your comment records have a 'comment' field
        ))}
      </div>
      <div>
        <h3>Comments</h3>
        <form onSubmit={submitComment}>
          <input
            type="text"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Write a comment..."
          />
          <button type="submit">Submit Comment</button>
        </form>
      </div>
    </div>
  );
};

export default DetailView;
