import React from 'react';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { supabase } from '../client';
import { Link } from 'react-router-dom';
import more from './components/more.png';

const DetailView = () => {
  const { id, name } = useParams();
  const [post, setPost] = useState({ id: null, name: "", flavor: "", frostingColor: "", toppings: "" });
  const [count, setCount] = useState(0);

  useEffect(() => {
    const fetchPost = async () => {
      const { data } = await supabase
        .from('Posts')
        .select()
        .eq('id', id)
        .single();

      setPost(data);
      setCount(data.price);
    }

    fetchPost();
  }, [id]);

  const updateCount = async (event) => {
    event.preventDefault();

    await supabase
      .from('Posts')
      .update({ price: count + 1 })
      .eq('id', id);

    setCount((count) => count + 1);
  }

  return (
    <div className="Card">
      <Link to={`/edit/${id}/${name}`}><img className="moreButton" alt="edit button" src={more} /></Link>
      <h2 className="title">{post.name}</h2>
      <h3 className="flavor">{"Flavor: " + post.flavor}</h3>
      <p className="frostingColor">{"Frosting Color: " + post.frostingColor}</p>
      <p className="toppings">{"Toppings: " + post.toppings}</p>
      <button className="betButton" onClick={updateCount} >üëç Price: {count}</button>
    </div>
  );
};

export default DetailView;
