import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import { Link } from 'react-router-dom';

const DetailView = () => {
  const { id, name } = useParams();
  const [post, setPost] = useState({});
  const [count, setCount] = useState(0);

  useEffect(() => {
    const fetchPost = async () => {
      const { data } = await supabase
        .from('Posts')
        .select()
        .eq('id', id)
        .single();

      setPost(data);
      setCount(data.price);
    }

    fetchPost();
  }, [id]);

  return (
    <div>
      <h2 className="title">{post.name}</h2>
      <h3 className="flavor">{"Flavor: " + post.flavor}</h3>
      <p className="frostingColor">{"Frosting Color: " + post.frostingColor}</p>
      <p className="toppings">{"Toppings: " + post.toppings}</p>
      {post.price && <p>Expensive!</p>}
      <p>üëç Price: {count}</p>
      <Link to={`/edit/${id}/${name}`}><button>Edit cupcake</button></Link>
    </div>
  );
};

export default DetailView;
