import React from 'react';
import { useState, useEffect } from 'react';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client';


const Card = ({ id, title, author, description }) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const fetchCount = async () => {
      const { data } = await supabase
        .from('Posts')
        .select('price')
        .eq('id', id)
        .single();

      setCount(data.price); //get price so need useeffect
    }

    fetchCount();
  }, [id]);

  const updateCount = async (event) => { //no need useeffect since we are updating the count
    event.preventDefault();

    await supabase
      .from('Posts')
      .update({ price: count + 1 })
      .eq('id', id);

    setCount((count) => count + 1);
  }

  return (
    <div className="Card">
      <Link to={'edit/' + id}><img className="moreButton" alt="edit button" src={more} /></Link>
      <h2 className="title">{title}</h2>
      <h3 className="author">{"by " + author}</h3>
      <p className="description">{description}</p>
      <button className="betButton" onClick={updateCount} >üëç Bet Count: {count}</button>
    </div>
  );
};

export default Card;
