import React from 'react';
import { useState, useEffect } from 'react';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client';


const Card = ({ id, name, flavor, frostingColor, toppings }) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const fetchCount = async () => {
      const { data } = await supabase
        .from('Posts')
        .select('price')
        .eq('id', id)
        .single();

      setCount(data.price); //get price so need useeffect
    }

    fetchCount();
  }, [id]);

  const updateCount = async (event) => { //no need useeffect since we are updating the count
    event.preventDefault();

    await supabase
      .from('Posts')
      .update({ price: count + 1 })
      .eq('id', id);

    setCount((count) => count + 1);
    console.log(`../images/${flavor}.png`)    
  }

  return (
    <Link to={`/detailview/${id}/${name}`}>

      <div className="Card">
        <Link to={`/edit/${id}/${name}`}><img className="moreButton" alt="edit button" src={more} /></Link>
        <img src={`../images/${flavor}.png`} alt={flavor} />        
        <h2 className="title">{name}</h2>
        <h3 className="flavor">{"Flavor: " + flavor}</h3>
        <p className="frostingColor">{"Frosting Color: " + frostingColor}</p>
        <p className="toppings">{"Toppings: " + toppings}</p>
        <button className="betButton" onClick={updateCount} >üëç Price: {count}</button>
      </div>
    </Link>

  );
};

export default Card;
